//Выезщающая панель
let closeCross = document.querySelector(".panel-btn-close");
let panel = document.querySelector(".panel");

let btnOpen = document.querySelector("#btn_open");
let btnClose = document.querySelector("#btn_close");

btnOpen.onclick = function () {
  panel.classList.add("shadowed");
};
btnClose.onclick = function () {
  closePanel();
};
closeCross.onclick = function () {
  closePanel();
};

function closePanel() {
  panel.classList.remove("shadowed");
}

//Ссылки меню
let sideLinks = document.querySelectorAll(".side-link");
for (let elem of sideLinks) {
  elem.addEventListener("click", () => {
    sideLinks.forEach((items) => items.classList.remove("active-link"));
    elem.classList.add("active-link");
  });
}
